                  </div>
                  <div id={"card-element-" + i} className="collapse">
                    <div className="card-body">
                    <table className="table">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Currency</th>
            <th scope="col">Amount</th>
            <th scope="col">% Portfolio</th>
            <th scope="col">SDG Global</th>
            <th scope="col">SDG Global Sector</th>
          </tr>
        </thead>
        <tbody>
      {items.map(function(item) {
        console.log(number)
        if(item.sector === number) {
          return (
          <tr>
            <td>{item.name}</td>
            <td>{item.currency}</td>
            <td>{item.amount}</td>
            <td>{item.portfolio}</td>
            <td style={item.sdgGlobal > item.sdgGlobalSector ? {backgroundColor : "green"} : {backgroundColor : "red"}}>{item.sdgGlobal}</td>
            <td>{item.sdgGlobalSector}</td>
          </tr>
        );
        }

    })}
        </tbody>
      </table>
                    </div>
                  </div>
  </div>
  );

  let datas = {
  datasets: [{
    data: [
      11,
      16,
      7,
      3,
      14
    ],
    backgroundColor: [
      '#FF6384',
      '#4BC0C0',
      '#FFCE56',
      '#E7E9ED',
      '#36A2EB'
    ],
    label: 'My dataset' // for legend
  }],
  labels: [
    'Red',
    'Green',
    'Yellow',
    'Grey',
    'Blue'
  ]
};

  return (
    <div className="container-fluid">
        <div className="row">
          <div className="col-md-12">
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#bs-example-navbar-collapse-1">
